class LocalStorageMock {
    store;
    length;
    constructor() {
        this.store = {};
        this.length = 0;
    }
    /**
     * @param idx https://developer.mozilla.org/en-US/docs/Web/API/Storage/key
     * @returns
     */
    key = (idx) => {
        const values = Object.values(this.store);
        return values[idx];
    };
    clear() {
        this.store = {};
    }
    getItem(key) {
        return this.store[key] || null;
    }
    setItem(key, value) {
        this.store[key] = String(value);
    }
    removeItem(key) {
        delete this.store[key];
    }
}
export default LocalStorageMock;
