const isSelected = (path, pathname) => pathname.includes(path);
export const isSelectedFromArray = (pathname, paths, tabPrefix) => {
    // console.log({ paths, pathname, tabPrefix });
    const f = paths.filter((x) => isSelected(tabPrefix + (x.path || ""), pathname));
    if (f.length) {
        return f[0].path || "";
    }
    return "";
};
export const getRoutePath = (path = "", allowsNested = true) => {
    return path + "/" + (allowsNested ? "*" : "");
};
