export const delay = (ms = 500) => new Promise((resolve) => setTimeout(resolve, ms));
/**
 * puts content into a file that is being downloaded
 * @param reportName https://stackoverflow.com/questions/35038884/download-file-from-bytes-in-javascript
 * @see fileName: filename of out
 * @param byte
 * @param type
 */
export const saveByteArray = (content, // Buffer
filename = "out", type = "application/text") => {
    const blob = new Blob([content], { type });
    const link = document.createElement("a");
    link.href = window.URL.createObjectURL(blob);
    link.setAttribute("download", filename);
    link.click();
};
export const yesOrNo = (d, labels = {
    yes: "yes",
    no: "no",
    notBoolean: "-",
}) => {
    if (typeof d !== "boolean") {
        return labels.notBoolean;
    }
    if (d === true) {
        return labels.yes;
    }
    return labels.no;
};
